import React, { useEffect, useRef } from 'react';
import { Search } from 'lucide-react';

const SearchPage = () => {
  const autoCompleteRef = useRef(null);
  const inputRef = useRef(null);

  useEffect(() => {
    const loadGoogleMapsScript = () => {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBUE_42ctF3i-1x77Wec5d3bSRLQpzzWN0&libraries=places`;
      script.async = true;
      script.defer = true;
      document.head.appendChild(script);
      return new Promise((resolve) => {
        script.onload = resolve;
      });
    };

    const initAutocomplete = () => {
      if (!window.google) return;

      autoCompleteRef.current = new window.google.maps.places.Autocomplete(
        inputRef.current,
        {
          types: ['establishment'],
          componentRestrictions: { country: 'nl' },
          fields: ['place_id', 'name', 'formatted_address', 'geometry']
        }
      );

      autoCompleteRef.current.addListener('place_changed', handlePlaceSelect);
    };

    loadGoogleMapsScript().then(() => {
      initAutocomplete();
    });
  }, []);

  const handlePlaceSelect = () => {
    const place = autoCompleteRef.current.getPlace();
    if (!place.geometry) {
      console.log("No details available for input: '" + place.name + "'");
      return;
    }

    const placeDetails = {
      name: place.name,
      address: place.formatted_address,
    };

    // Navigate to results page with venue details
    window.location.href = `/results?name=${encodeURIComponent(placeDetails.name)}&address=${encodeURIComponent(placeDetails.address)}`;
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-bold text-center text-blue-600 mb-12">GymSpotCheck</h1>

        <div className="relative mb-8">
          <input
            ref={inputRef}
            type="text"
            placeholder="Search for a gym"
            className="w-full p-4 pr-12 text-gray-900 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
          <Search className="absolute right-4 top-1/2 transform -translate-y-1/2 text-gray-400" />
        </div>
      </div>
    </div>
  );
};

export default SearchPage;
