import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const ResultsPage = () => {
  const [trafficData, setTrafficData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchData = async () => {
      const searchParams = new URLSearchParams(location.search);
      const venueName = searchParams.get('name');
      const venueAddress = searchParams.get('address');

      if (!venueName || !venueAddress) {
        setError('Venue information is missing.');
        setLoading(false);
        return;
      }

      try {
        const apiUrl = `https://besttime.app/api/v1/forecasts`;
        const params = new URLSearchParams({
          'api_key_private': 'pri_070565f833f9459aad223978a7a19b74',
          'venue_name': venueName,
          'venue_address': venueAddress
        });

        console.log('Fetching data from:', `${apiUrl}?${params}`);

        const response = await fetch(`${apiUrl}?${params}`, {
          method: 'POST',
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('API Response:', data);

        if (data.status === 'OK') {
          setTrafficData(data);
        } else {
          setError(data.message || 'Failed to fetch data');
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        setError(err.message || 'An error occurred while fetching data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [location.search]);

  const getCurrentDayData = () => {
    if (!trafficData || !trafficData.analysis) {
      console.log('No traffic data available');
      return null;
    }

    const amsterdamTime = new Date().toLocaleString("en-US", {timeZone: "Europe/Amsterdam"});
    const currentDay = new Date(amsterdamTime).getDay();
    const dayNames = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const currentDayName = dayNames[currentDay];

    console.log('Current day:', currentDayName);

    const dayData = trafficData.analysis.find(day => day.day_info.day_text === currentDayName);

    if (!dayData) {
      console.log('No data found for current day');
      return null;
    }

    // The API provides data starting from 6 AM of the current day to 5 AM of the next day
    return dayData.day_raw.map((value, index) => {
      const hour = (index + 6) % 24; // Adjust index to start from 6 AM
      return {
        hour,
        traffic: value,
        label: `${hour}:00`
      };
    });
  };

  const renderForecastChart = () => {
    const chartData = getCurrentDayData();
    if (!chartData) {
      console.log('No chart data available');
      return <p>No data available for today</p>;
    }

    const currentHour = new Date().getHours();

    return (
      <ResponsiveContainer width="100%" height={400}>
        <BarChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" vertical={false} />
          <XAxis 
            dataKey="label" 
            interval={2}
            tick={{ fontSize: 12 }}
          />
          <YAxis 
            domain={[0, 100]}
            label={{ value: 'Foot Traffic %', angle: -90, position: 'insideLeft' }}
          />
          <Tooltip 
            formatter={(value, name) => [`${value}%`, 'Foot Traffic']}
            labelFormatter={(label) => `${label}`}
          />
          <Bar 
            dataKey="traffic" 
            fill="#8884d8" 
            barSize={20}
            shape={(props) => {
              const { x, y, width, height, payload } = props;
              return (
                <rect 
                  x={x} 
                  y={y} 
                  width={width} 
                  height={height} 
                  fill={payload.hour === currentHour ? '#ff7f50' : '#8884d8'}
                  opacity={0.8}
                />
              );
            }}
          />
        </BarChart>
      </ResponsiveContainer>
    );
  };

  const renderVenueInfo = () => {
    if (!trafficData || !trafficData.venue_info) {
      console.log('No venue info available');
      return null;
    }

    const { venue_name, venue_address, venue_timezone } = trafficData.venue_info;

    return (
      <div className="mb-4">
        <h2 className="text-xl font-semibold mb-2">Venue Information</h2>
        <p><strong>Name:</strong> {venue_name}</p>
        <p><strong>Address:</strong> {venue_address}</p>
        <p><strong>Timezone:</strong> {venue_timezone}</p>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto">
        <button
          onClick={() => navigate('/')}
          className="mb-6 text-blue-600 hover:text-blue-800"
        >
          &larr; Back to Search
        </button>

        {loading && (
          <div className="text-center text-gray-600">
            Loading traffic data...
          </div>
        )}

        {error && (
          <div className="text-center text-red-500 mb-4">
            <p>Error: {error}</p>
          </div>
        )}

        {trafficData && !loading && !error && (
          <div className="bg-white rounded-lg shadow-md p-6">
            {renderVenueInfo()}
            <h2 className="text-xl font-semibold mb-4">
              Today's popular visiting hours
            </h2>
            {renderForecastChart()}
          </div>
        )}

        <div className="mt-8 bg-gray-200 p-4 rounded">
          <h2 className="text-lg font-semibold mb-2">Debug Information</h2>
          <pre className="whitespace-pre-wrap overflow-auto max-h-96">
            {JSON.stringify({ trafficData, loading, error }, null, 2)}
          </pre>
        </div>
      </div>
    </div>
  );
};

export default ResultsPage;
