import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const ResultsPage = () => {
  const [trafficData, setTrafficData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const venueName = searchParams.get('name');
    const venueAddress = searchParams.get('address');

    if (!venueName || !venueAddress) {
      setError('Venue information is missing.');
      setLoading(false);
      return;
    }

    const fetchTrafficData = async () => {
      try {
        const response = await fetch(`https://besttime.app/api/v1/forecasts?api_key_private=pri_070565f833f9459aad223978a7a19b74&venue_name=${encodeURIComponent(venueName)}&venue_address=${encodeURIComponent(venueAddress)}`);
        const data = await response.json();

        if (data.status !== 'OK') {
          throw new Error(data.message || 'Failed to fetch forecast data');
        }

        setTrafficData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTrafficData();
  }, [location.search]);

  const renderForecastChart = () => {
    if (!trafficData || !trafficData.analysis || !trafficData.analysis.week_raw) return null;

    const chartData = trafficData.analysis.week_raw.flatMap((day, dayIndex) =>
      day.day_raw.map((hour, hourIndex) => ({
        time: `${['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'][dayIndex]} ${hourIndex}:00`,
        intensity: hour.intensity,
      }))
    );

    return (
      <ResponsiveContainer width="100%" height={400}>
        <BarChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="time" tick={{ fontSize: 10 }} interval={23} />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="intensity" fill="#8884d8" name="Foot Traffic Intensity" />
        </BarChart>
      </ResponsiveContainer>
    );
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto">
        <button
          onClick={() => navigate('/')}
          className="mb-6 text-blue-600 hover:text-blue-800"
        >
          &larr; Back to Search
        </button>

        {loading && <p className="text-center">Loading...</p>}

        {error && <p className="text-center text-red-500">{error}</p>}

        {trafficData && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <h1 className="text-2xl font-bold mb-4">{trafficData.venue_info.venue_name}</h1>
            <p className="mb-4">{trafficData.venue_info.venue_address}</p>
            <h2 className="text-xl font-semibold mb-4">Foot Traffic Forecast</h2>
            {renderForecastChart()}
          </div>
        )}
      </div>
    </div>
  );
};

export default ResultsPage;
