import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { QuestionMarkCircleIcon } from '@heroicons/react/24/solid';

const GYM_EQUIPMENT = [
  { 
    name: 'Cardio Zone', 
    percentage: 40,
    subcategories: [
      'Treadmill section',
      'Elliptical machine section',
      'Rowing machine area',
      'Stationary bikes',
      'Stair climbers'
    ]
  },
  { 
    name: 'Functional Training', 
    percentage: 30,
    subcategories: [
      'Functional training zone',
      'CrossFit area',
      'TRX/Suspension training area',
      'Plyometric boxes',
      'Medicine ball area'
    ]
  },
  { 
    name: 'Strength Training', 
    percentage: 12,
    subcategories: [
      'Free weights area',
      'Machine weights area',
      'Power lifting platform',
      'Squat rack zone',
      'Bench press area',
      'Cable machine section',
      'Smith machine area'
    ]
  },
  { 
    name: 'Group Fitness', 
    percentage: 12,
    subcategories: [
      'Group fitness/Class studios',
      'Spinning/Cycling studio',
      'Yoga studio',
      'Pilates studio',
      'Aerobics room'
    ]
  },
  { 
    name: 'Amenities', 
    percentage: 8,
    subcategories: [
      'Locker rooms',
      'Showers',
      'Changing areas'
    ]
  },
];

const ResultsPage = () => {
  const [trafficData, setTrafficData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showDebug, setShowDebug] = useState(false);
  const [isGymClosed, setIsGymClosed] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchData = async () => {
      const searchParams = new URLSearchParams(location.search);
      const venueName = searchParams.get('name');
      const venueAddress = searchParams.get('address');

      console.log('Fetching data for:', venueName, venueAddress);

      if (!venueName || !venueAddress) {
        setError('Venue information is missing.');
        setLoading(false);
        return;
      }

      try {
        const apiUrl = `https://besttime.app/api/v1/forecasts`;
        const params = new URLSearchParams({
          'api_key_private': 'pri_070565f833f9459aad223978a7a19b74',
          'venue_name': venueName,
          'venue_address': venueAddress
        });

        console.log('Fetching data from:', `${apiUrl}?${params}`);

        const response = await fetch(`${apiUrl}?${params}`, {
          method: 'POST',
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('API Response:', data);

        if (data.status === 'OK') {
          setTrafficData(data);
        } else {
          throw new Error(data.message || 'Failed to fetch data');
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        setError(err.message || 'An error occurred while fetching data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [location.search]);

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.shiftKey) {
        const handleDoubleClick = () => {
          setShowDebug(prev => !prev);
          document.removeEventListener('dblclick', handleDoubleClick);
        };
        document.addEventListener('dblclick', handleDoubleClick);
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => {
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, []);

  const getCurrentDayData = () => {
    if (!trafficData || !trafficData.analysis) {
      console.log('No traffic data available');
      return null;
    }

    const amsterdamTime = new Date().toLocaleString("en-US", {timeZone: "Europe/Amsterdam"});
    const currentDay = new Date(amsterdamTime).getDay();
    const dayNames = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const currentDayName = dayNames[currentDay];

    console.log('Current day:', currentDayName);

    const dayData = trafficData.analysis.find(day => day.day_info.day_text === currentDayName);

    if (!dayData) {
      console.log('No data found for current day');
      return null;
    }

    return dayData.day_raw.map((value, index) => ({
      hour: index,
      traffic: value,
      label: `${index}:00`
    }));
  };

  const getOpeningHours = () => {
    if (!trafficData || !trafficData.analysis) return '';
    const currentDayData = trafficData.analysis.find(day => day.day_info.day_text === new Date().toLocaleString('en-US', { weekday: 'long' }));
    if (!currentDayData || !currentDayData.day_info) return '';

    const openHour = currentDayData.day_info.venue_open;
    const closeHour = currentDayData.day_info.venue_closed;

    const formatHour = (hour) => {
      if (hour === 24) return '12am';
      if (hour === 12) return '12pm';
      return hour > 12 ? `${hour - 12}pm` : `${hour}am`;
    };

    return `${formatHour(openHour)}–${formatHour(closeHour)}`;
  };

  const checkIfGymClosed = () => {
    if (!trafficData || !trafficData.analysis) return true;
    const currentHour = new Date().getHours();
    const currentDayData = trafficData.analysis.find(day => day.day_info.day_text === new Date().toLocaleString('en-US', { weekday: 'long' }));
    if (!currentDayData || !currentDayData.day_info) return true;

    const openHour = currentDayData.day_info.venue_open;
    const closeHour = currentDayData.day_info.venue_closed;

    return currentHour < openHour || currentHour >= closeHour;
  };

  const renderForecastChart = () => {
    const chartData = getCurrentDayData();
    if (!chartData) {
      console.log('No chart data available');
      return <p>No data available for today</p>;
    }

    return (
      <ResponsiveContainer width="100%" height={400}>
        <BarChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="label" />
          <YAxis />
          <Tooltip />
          <Bar dataKey="traffic" fill="#8884d8" />
        </BarChart>
      </ResponsiveContainer>
    );
  };

  const renderVenueInfo = () => {
    if (!trafficData || !trafficData.venue_info) {
      console.log('No venue info available');
      return null;
    }

    const { venue_name, venue_address, venue_timezone } = trafficData.venue_info;
    const openingHours = getOpeningHours();

    return (
      <div className="mb-4">
        <h2 className="text-xl font-semibold mb-2">Gym Information</h2>
        <div className="flex items-center">
          <p className="font-bold">{venue_name}</p>
          {isGymClosed && <span className="ml-2 bg-red-500 text-white text-xs font-bold px-2 py-1 rounded">CLOSED</span>}
        </div>
        <p><strong>Address:</strong> {venue_address}</p>
        <p><strong>Timezone:</strong> {venue_timezone}</p>
        {openingHours && <p><strong>Hours:</strong> {openingHours}</p>}
      </div>
    );
  };

  const getCurrentTrafficPercentage = () => {
    if (!trafficData || !trafficData.analysis) return 0;
    const currentHour = new Date().getHours();
    const currentDayData = getCurrentDayData();
    return currentDayData ? currentDayData.find(data => data.hour === currentHour)?.traffic || 0 : 0;
  };

  const renderEquipmentUsageCards = () => {
    const currentTrafficPercentage = getCurrentTrafficPercentage();

    return (
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-4 mb-6">
        {GYM_EQUIPMENT.map((equipment, index) => {
          const usage = (equipment.percentage / 100) * currentTrafficPercentage;
          return (
            <div key={index} className="bg-white rounded-lg shadow-md p-4 relative">
              <h3 className="text-lg font-semibold mb-2">{equipment.name}</h3>
              <p className="text-3xl font-bold text-blue-600">{usage.toFixed(1)}%</p>
              <div className="absolute top-2 right-2 group">
                <QuestionMarkCircleIcon className="h-5 w-5 text-gray-400 hover:text-gray-600" />
                <div className="hidden group-hover:block absolute right-0 w-48 p-2 bg-white border rounded shadow-lg z-10">
                  <ul className="list-disc pl-4 text-sm">
                    {equipment.subcategories.map((sub, idx) => (
                      <li key={idx}>{sub}</li>
                    ))}
                  </ul>
                </div>
              </div>
              {isGymClosed && (
                <div className="absolute inset-0 bg-gray-200 bg-opacity-75 flex items-center justify-center">
                  <span className="text-red-600 font-bold text-xl transform -rotate-45">CLOSED</span>
                </div>
              )}
            </div>
          );
        })}
      </div>
    );
  };

  useEffect(() => {
    if (trafficData) {
      setIsGymClosed(checkIfGymClosed());
    }
  }, [trafficData]);

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-6xl mx-auto">
        <button
          onClick={() => navigate('/')}
          className="mb-6 text-blue-600 hover:text-blue-800"
        >
          &larr; Back to Search
        </button>

        {loading && (
          <div className="text-center text-gray-600">
            Loading traffic data...
          </div>
        )}

        {error && (
          <div className="text-center text-red-500 mb-4">
            <p>Error: {error}</p>
          </div>
        )}

        {trafficData && !loading && !error && (
          <div className="bg-white rounded-lg shadow-md p-6">
            {renderVenueInfo()}
            <h2 className="text-xl font-semibold mb-4">Gym Equipment Usage</h2>
            {renderEquipmentUsageCards()}
            <h2 className="text-xl font-semibold mb-4">Today's popular visiting hours</h2>
            {renderForecastChart()}
          </div>
        )}

        {showDebug && (
          <div className="mt-8 bg-gray-200 p-4 rounded">
            <h2 className="text-lg font-semibold mb-2">Debug Information</h2>
            <pre className="whitespace-pre-wrap overflow-auto max-h-96">
              {JSON.stringify({ trafficData, loading, error, isGymClosed }, null, 2)}
            </pre>
          </div>
        )}
      </div>
    </div>
  );
};

export default ResultsPage;
