import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const ResultsPage = () => {
  const [trafficData, setTrafficData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [debugInfo, setDebugInfo] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const venueName = searchParams.get('name');
    const venueAddress = searchParams.get('address');

    if (!venueName || !venueAddress) {
      setError('Venue information is missing.');
      setLoading(false);
      return;
    }

    const fetchTrafficData = async () => {
      setDebugInfo(null);
      try {
        const apiKey = 'pri_070565f833f9459aad223978a7a19b74';
        const besttimeUrl = `https://besttime.app/api/v1/forecasts?` + 
          new URLSearchParams({
            api_key_private: apiKey,
            venue_name: venueName,
            venue_address: venueAddress
          });

        setDebugInfo(prev => ({ ...prev, besttimeUrl, venueName, venueAddress }));

        const response = await fetch(besttimeUrl);
        const data = await response.json();

        setDebugInfo(prev => ({ ...prev, apiResponse: data, status: response.status }));

        if (data.status !== 'OK') {
          throw new Error(data.message || 'Failed to fetch forecast data');
        }

        setTrafficData(data);
      } catch (err) {
        setError(err.message || 'Failed to fetch traffic data. Please try again later.');
        console.error('API Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTrafficData();
  }, [location.search]);

  const renderForecastChart = () => {
    // ... (chart rendering logic remains the same)
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto">
        <button
          onClick={() => navigate('/')}
          className="mb-6 text-blue-600 hover:text-blue-800"
        >
          &larr; Back to Search
        </button>

        {loading && (
          <div className="text-center text-gray-600">
            Loading traffic data...
          </div>
        )}

        {error && (
          <div className="text-center text-red-500 mb-4">
            <p>Error: {error}</p>
            <p className="mt-2">The venue might not be in our database or there might be an issue with the API. Please try searching for a different venue.</p>
          </div>
        )}

        {trafficData && !loading && !error && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <h1 className="text-2xl font-bold mb-4">{trafficData.venue_info.venue_name}</h1>
            <div className="mb-4">
              <h2 className="text-lg font-semibold mb-2">Address</h2>
              <p>{trafficData.venue_info.venue_address}</p>
            </div>
            <div className="mb-4">
              <h2 className="text-lg font-semibold mb-2">Foot Traffic Forecast</h2>
              {renderForecastChart()}
            </div>
          </div>
        )}

        {debugInfo && (
          <div className="mt-8 bg-gray-200 p-4 rounded">
            <h2 className="text-lg font-semibold mb-2">Debug Information</h2>
            <pre className="whitespace-pre-wrap overflow-auto max-h-96">{JSON.stringify(debugInfo, null, 2)}</pre>
          </div>
        )}
      </div>
    </div>
  );
};

export default ResultsPage;
